import { useParams } from "react-router-dom";
import PeopleInformation from "../components/PeopleInformation";
import { useEffect, useState } from "react";
import axios from "../utils/axios";
import { API_URL } from "../consts";
import Modal from "../components/Modal";
import TransactionForm from "../components/TransactionForm";

function Transactions () {
    const { identification } = useParams();

    const [people, setPeople] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [showModal, setShowModal] = useState(false);

    useEffect(()=>{
        axios.get(`${API_URL}/people/${identification}/`)
            .then((res) => {
                setPeople(res.data);
                    axios.get(`${API_URL}/transactions/people/${res.data.id}/`)
                .then((res) => {
                    setTransactions(res.data);
                })
                .catch((e) => console.error(e));
            })
            .catch((e) => console.error(e));
        
    }, [identification]);

    const toggleModal = () => {
        setShowModal((oldShow) => !oldShow);
    }

    return (
        <>
        {people && (<PeopleInformation people={people}/>)}

        <button>Agregar</button>

        <div>
            {transactions.length ? (
                <table>
                    <thead>
                        <tr>
                            <th colSpan={4}>  
                                TRANSACCIONES
                            </th>
                        </tr>
                        <tr>
                            <th>ID</th>
                            <th>Fecha de inicio</th>
                            <th>Fecha de finalizacion</th>
                            <th>Valor</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map((transaction)=>{
                            return(
                                <tr>
                                    <td>{transaction.id}</td>
                                    <td>{(new Date(transaction.startDate)).toLocaleString().split(',')[0]}</td>
                                    <td>{(new Date(transaction.endDate)).toLocaleString().split(',')[0]}</td>
                                    <td>{transaction.value}</td>
                                </tr>
                            )
                        })}
                    </tbody>

                </table>
            ) : (
                <p>No hay transacciones realizadas...</p>
            )}
            
        </div>

        {showModal && (
          <Modal onClose={toggleModal}>
            <TransactionForm setTransactions={setTransactions} operation="Agregar" toggleModal={toggleModalCreate} />
          </Modal>
        )}

        </>
        
    )
    
}

export default Transactions;
